// lib/models/user_model.dart
class User {
  final int id; // Unique ID, can be generated (e.g., timestamp + random)
  final String name;
  final DateTime createdAt;

  User({
    required this.id,
    required this.name,
    required this.createdAt,
  });

  // Convert a User object into a Map. The keys must match the column names in the database.
  Map<String, dynamic> toMap() {
    return {
      'id': id,
      'name': name,
      'created_at': createdAt.toIso8601String(), // Store as ISO 8601 string
    };
  }

  // Extract a User object from a Map.
  factory User.fromMap(Map<String, dynamic> map) {
    return User(
      id: map['id'],
      name: map['name'],
      createdAt: DateTime.parse(map['created_at']),
    );
  }

  @override
  String toString() {
    return 'User(id: $id, name: $name, createdAt: $createdAt)';
  }
}

// lib/models/task_model.dart

enum TaskType { daily, temporary }

class Task {
  final int id; // Unique ID for the task
  final String name;
  final TaskType type;
  final int durationDays; // Applicable for daily tasks
  final DateTime createdAt;
  final bool isActive;

  Task({
    required this.id,
    required this.name,
    required this.type,
    this.durationDays = 0, // Default to 0 for temporary tasks
    required this.createdAt,
    this.isActive = true,
  });

  // Convert a Task object into a Map.
  Map<String, dynamic> toMap() {
    return {
      'id': id,
      'name': name,
      'type': type.toString().split('.').last, // Store enum as string
      'duration_days': durationDays,
      'created_at': createdAt.toIso8601String(),
      'is_active': isActive ? 1 : 0, // Store boolean as integer
    };
  }

  // Extract a Task object from a Map.
  factory Task.fromMap(Map<String, dynamic> map) {
    return Task(
      id: map['id'],
      name: map['name'],
      type: TaskType.values.firstWhere(
        (e) => e.toString().split('.').last == map['type'],
        orElse: () => TaskType.daily, // Default value if not found
      ),
      durationDays: map['duration_days'],
      createdAt: DateTime.parse(map['created_at']),
      isActive: map['is_active'] == 1,
    );
  }

  @override
  String toString() {
    return 'Task(id: $id, name: $name, type: $type, durationDays: $durationDays, createdAt: $createdAt, isActive: $isActive)';
  }
}

// lib/models/day_record_model.dart

enum VisualState {
  empty,
  lightGreen,
  green,
  darkGreen,
  cheat,
  star,
}

class DayRecord {
  final String date; // YYYY-MM-DD format for easy storage and retrieval
  final List<int> completedTaskIds; // IDs of tasks completed on this day
  final bool cheatUsed;
  final double completionScore; // 0.0 to 1.0
  final VisualState visualState;

  DayRecord({
    required this.date,
    required this.completedTaskIds,
    this.cheatUsed = false,
    this.completionScore = 0.0,
    this.visualState = VisualState.empty,
  });

  // Convert a DayRecord object into a Map.
  Map<String, dynamic> toMap() {
    return {
      'date': date,
      'completed_task_ids': completedTaskIds.join(','), // Store as comma-separated string
      'cheat_used': cheatUsed ? 1 : 0,
      'completion_score': completionScore,
      'visual_state': visualState.toString().split('.').last, // Store enum as string
    };
  }

  // Extract a DayRecord object from a Map.
  factory DayRecord.fromMap(Map<String, dynamic> map) {
    return DayRecord(
      date: map['date'],
      completedTaskIds: map['completed_task_ids'] != null && map['completed_task_ids'].isNotEmpty
          ? List<int>.from(map['completed_task_ids'].split(',').map((id) => int.parse(id)))
          : [],
      cheatUsed: map['cheat_used'] == 1,
      completionScore: map['completion_score'],
      visualState: VisualState.values.firstWhere(
        (e) => e.toString().split('.').last == map['visual_state'],
        orElse: () => VisualState.empty, // Default value if not found
      ),
    );
  }

  @override
  String toString() {
    return 'DayRecord(date: $date, completedTaskIds: $completedTaskIds, cheatUsed: $cheatUsed, completionScore: $completionScore, visualState: $visualState)';
  }
}

// lib/services/scoring_service.dart

import '../models/day_record_model.dart';
import 'dart:math'; // For min function

class ScoringService {
  /// Calculates the core daily score based on completed and expected tasks.
  /// Returns a value between 0.0 and 1.0.
  double calculateCoreDailyScore(int completedTasksCount, int totalExpectedTasksCount) {
    if (totalExpectedTasksCount == 0) {
      return 0.0; // No tasks, no score. Or handle as per specific rule.
    }
    return completedTasksCount / totalExpectedTasksCount;
  }

  /// Calculates credit from temporary tasks, capped at 50% of missing daily work.
  double calculateTempTaskCredit(int tempTasksCompleted, int missingDailyTasksCount) {
    if (missingDailyTasksCount <= 0 || tempTasksCompleted <= 0) {
      return 0.0;
    }
    // Temporary tasks can cover at most 50% of missing daily work.
    return min(tempTasksCompleted.toDouble(), missingDailyTasksCount * 0.5);
  }

  /// Determines the visual state for a DayRecord based on score, cheat status, and overachievement.
  VisualState mapScoreToVisualState({
    required double finalScore,
    required bool cheatUsed,
    required bool hasStar,
  }) {
    if (cheatUsed) {
      return VisualState.cheat;
    }
    if (hasStar) {
      return VisualState.star;
    }

    if (finalScore >= 1.0) {
      return VisualState.darkGreen;
    } else if (finalScore >= 0.76) {
      return VisualState.green;
    } else if (finalScore >= 0.51) {
      return VisualState.lightGreen;
    } else if (finalScore >= 0.26) {
      return VisualState.lightGreen; // Example: can differentiate shades later
    } else if (finalScore > 0.0) {
      return VisualState.empty; // Represents minimal effort but not empty
    } else {
      return VisualState.empty;
    }
  }

  /// Calculates the overall completion score for a day.
  /// Combines core daily tasks and temporary task compensation.
  double calculateOverallCompletionScore({
    required int completedDailyTasks,
    required int totalDailyTasks,
    required int completedTemporaryTasks,
  }) {
    if (totalDailyTasks == 0) {
      // If there are no daily tasks, only temporary tasks count towards an "overachievement"
      // or a base score, but the core score cannot be calculated normally.
      // For now, return 0.0, as per the plan's focus on daily tasks.
      // This logic might need refinement based on exact requirements for days with no daily tasks.
      return 0.0;
    }

    final double coreScore = calculateCoreDailyScore(completedDailyTasks, totalDailyTasks);

    int missingDailyTasks = totalDailyTasks - completedDailyTasks;
    double tempCredit = calculateTempTaskCredit(completedTemporaryTasks, missingDailyTasks);

    double effectiveCompleted = completedDailyTasks + tempCredit;
    double adjustedScore = effectiveCompleted / totalDailyTasks;

    // Clamp score to a maximum of 1.0 for regular scoring before star logic
    return min(adjustedScore, 1.0);
  }

  /// Determines if a day qualifies for a "star" visual state.
  bool checkForStarStatus({
    required int completedDailyTasks,
    required int totalDailyTasks,
    required int completedTemporaryTasks,
  }) {
    // Star status if all daily tasks are done AND there are bonus temporary tasks
    return completedDailyTasks >= totalDailyTasks && completedTemporaryTasks > 0;
  }
}

// lib/services/database_service.dart

import 'package:sqflite/sqflite.dart';
import 'package:path/path.dart';
import 'package:path_provider/path_provider.dart'; // For getting the application documents directory
import 'dart:io'; // For Directory

import '../models/user_model.dart';
import '../models/task_model.dart';
import '../models/day_record_model.dart';

class DatabaseService {
  static Database? _database;
  static final DatabaseService instance = DatabaseService._constructor();

  final String usersTable = 'users';
  final String tasksTable = 'tasks';
  final String dayRecordsTable = 'day_records';

  DatabaseService._constructor();

  Future<Database> get database async {
    if (_database != null) return _database!;
    _database = await _initDatabase();
    return _database!;
  }

  Future<Database> _initDatabase() async {
    Directory documentsDirectory = await getApplicationDocumentsDirectory();
    String path = join(documentsDirectory.path, 'consistency_tracker.db');
    return await openDatabase(
      path,
      version: 1,
      onCreate: _onCreate,
    );
  }

  Future _onCreate(Database db, int version) async {
    await db.execute('''
      CREATE TABLE $usersTable (
        id INTEGER PRIMARY KEY,
        name TEXT NOT NULL,
        created_at TEXT NOT NULL
      )
    ''');
    await db.execute('''
      CREATE TABLE $tasksTable (
        id INTEGER PRIMARY KEY,
        name TEXT NOT NULL,
        type TEXT NOT NULL,
        duration_days INTEGER NOT NULL,
        created_at TEXT NOT NULL,
        is_active INTEGER NOT NULL
      )
    ''');
    await db.execute('''
      CREATE TABLE $dayRecordsTable (
        date TEXT PRIMARY KEY,
        completed_task_ids TEXT NOT NULL,
        cheat_used INTEGER NOT NULL,
        completion_score REAL NOT NULL,
        visual_state TEXT NOT NULL
      )
    ''');
  }

  // --- User Management ---
  Future<int> createUser(User user) async {
    Database db = await instance.database;
    return await db.insert(usersTable, user.toMap());
  }

  Future<User?> getUser(int id) async {
    Database db = await instance.database;
    List<Map<String, dynamic>> maps = await db.query(
      usersTable,
      where: 'id = ?',
      whereArgs: [id],
    );
    if (maps.isNotEmpty) {
      return User.fromMap(maps.first);
    }
    return null;
  }

  // --- Task Management ---
  Future<int> addTask(Task task) async {
    Database db = await instance.database;
    return await db.insert(tasksTable, task.toMap());
  }

  Future<int> updateTask(Task task) async {
    Database db = await instance.database;
    return await db.update(
      tasksTable,
      task.toMap(),
      where: 'id = ?',
      whereArgs: [task.id],
    );
  }

  Future<int> deleteTask(int id) async {
    Database db = await instance.database;
    return await db.delete(
      tasksTable,
      where: 'id = ?',
      whereArgs: [id],
    );
  }

  Future<List<Task>> getActiveTasksForDate(DateTime date) async {
    // This method will need more complex logic later to determine active tasks based on date and duration.
    // For now, return all active tasks.
    Database db = await instance.database;
    List<Map<String, dynamic>> maps = await db.query(
      tasksTable,
      where: 'is_active = ?',
      whereArgs: [1], // Assuming 1 for true, 0 for false
    );
    return List.generate(maps.length, (i) {
      return Task.fromMap(maps[i]);
    });
  }

  // --- DayRecord Management ---
  Future<int> createOrUpdateDayRecord(DayRecord record) async {
    Database db = await instance.database;
    // Attempt to update, if no rows affected, then insert
    int count = await db.update(
      dayRecordsTable,
      record.toMap(),
      where: 'date = ?',
      whereArgs: [record.date],
    );
    if (count == 0) {
      return await db.insert(dayRecordsTable, record.toMap());
    }
    return count;
  }

  Future<DayRecord?> getDayRecord(String date) async {
    Database db = await instance.database;
    List<Map<String, dynamic>> maps = await db.query(
      dayRecordsTable,
      where: 'date = ?',
      whereArgs: [date],
    );
    if (maps.isNotEmpty) {
      return DayRecord.fromMap(maps.first);
    }
    return null;
  }

  Future<List<DayRecord>> getDayRecords({int limit = 365}) async {
    Database db = await instance.database;
    List<Map<String, dynamic>> maps = await db.query(
      dayRecordsTable,
      orderBy: 'date DESC', // Get most recent records first
      limit: limit,
    );
    return List.generate(maps.length, (i) {
      return DayRecord.fromMap(maps[i]);
    });
  }
}

