import 'package:flutter/material.dart';
import 'package:consistency_tracker_v1/services/database_service.dart';
import 'package:consistency_tracker_v1/screens/first_run_setup_screen.dart';
import 'package:consistency_tracker_v1/screens/home_screen.dart';

void main() {
  WidgetsFlutterBinding.ensureInitialized();
  runApp(const MyApp());
}

class MyApp extends StatefulWidget {
  const MyApp({super.key});

  @override
  State<MyApp> createState() => _MyAppState();
}

class _MyAppState extends State<MyApp> {
  late Future<bool> _isFirstRun;

  @override
  void initState() {
    super.initState();
    _isFirstRun = _checkFirstRun();
  }

  Future<bool> _checkFirstRun() async {
    // We assume the first user created will have ID 1 (or any fixed ID for simplicity)
    // In a real app, you might query for any user or a specific configuration setting.
    final user = await DatabaseService.instance.getUser(1); // Try to get user with ID 1
    return user == null; // If user is null, it's a first run
  }

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Consistency Tracker',
      theme: ThemeData(
        primarySwatch: Colors.blueGrey,
        visualDensity: VisualDensity.adaptivePlatformDensity,
      ),
      home: FutureBuilder<bool>(
        future: _isFirstRun,
        builder: (context, snapshot) {
          if (snapshot.connectionState == ConnectionState.waiting) {
            return const Scaffold(
              body: Center(
                child: CircularProgressIndicator(),
              ),
            );
          } else if (snapshot.hasError) {
            return Scaffold(
              body: Center(
                child: Text('Error: ${snapshot.error}'),
              ),
            );
          } else {
            // If it's the first run, show the setup screen.
            // Otherwise, show the home screen.
            if (snapshot.data == true) {
              return const FirstRunSetupScreen();
            } else {
              return const HomeScreen();
            }
          }
        },
      ),
    );
  }
}

