import 'package:flutter/material.dart';
import 'package:consistency_tracker_v1/services/database_service.dart';
import 'package:consistency_tracker_v1/models/task_model.dart';

class TaskFormScreen extends StatefulWidget {
  const TaskFormScreen({super.key});

  @override
  State<TaskFormScreen> createState() => _TaskFormScreenState();
}

class _TaskFormScreenState extends State<TaskFormScreen> {
  final _formKey = GlobalKey<FormState>();
  final _nameController = TextEditingController();
  TaskType _taskType = TaskType.daily;
  final _durationController = TextEditingController(text: '1'); // Default to 1 day for daily tasks

  @override
  void dispose() {
    _nameController.dispose();
    _durationController.dispose();
    super.dispose();
  }

  Future<void> _saveTask() async {
    if (_formKey.currentState!.validate()) {
      final String taskName = _nameController.text.trim();
      final int duration = int.tryParse(_durationController.text) ?? 0;

      if (taskName.isNotEmpty && (_taskType == TaskType.temporary || duration > 0)) {
        final Task newTask = Task(
          id: DateTime.now().millisecondsSinceEpoch, // Simple ID generation
          name: taskName,
          type: _taskType,
          durationDays: _taskType == TaskType.daily ? duration : 0,
          createdAt: DateTime.now(),
          isActive: true,
        );

        await DatabaseService.instance.addTask(newTask);

        if (mounted) {
          ScaffoldMessenger.of(context).showSnackBar(
            SnackBar(content: Text('Task "${newTask.name}" saved!')),
          );
          Navigator.of(context).pop(); // Go back to the previous screen
        }
      }
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('Add New Task'),
      ),
      body: Padding(
        padding: const EdgeInsets.all(16.0),
        child: Form(
          key: _formKey,
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.stretch,
            children: <Widget>[
              TextFormField(
                controller: _nameController,
                decoration: const InputDecoration(
                  labelText: 'Task Name',
                  border: OutlineInputBorder(),
                ),
                validator: (value) {
                  if (value == null || value.isEmpty) {
                    return 'Please enter a task name';
                  }
                  return null;
                },
              ),
              const SizedBox(height: 20),
              Row(
                children: <Widget>[
                  Expanded(
                    child: RadioListTile<TaskType>(
                      title: const Text('Daily Task'),
                      value: TaskType.daily,
                      groupValue: _taskType,
                      onChanged: (TaskType? value) {
                        setState(() {
                          _taskType = value!;
                        });
                      },
                    ),
                  ),
                  Expanded(
                    child: RadioListTile<TaskType>(
                      title: const Text('Temporary Task'),
                      value: TaskType.temporary,
                      groupValue: _taskType,
                      onChanged: (TaskType? value) {
                        setState(() {
                          _taskType = value!;
                        });
                      },
                    ),
                  ),
                ],
              ),
              if (_taskType == TaskType.daily) ...[
                const SizedBox(height: 20),
                TextFormField(
                  controller: _durationController,
                  decoration: const InputDecoration(
                    labelText: 'Duration in Days (e.g., 30, 7)',
                    border: OutlineInputBorder(),
                  ),
                  keyboardType: TextInputType.number,
                  validator: (value) {
                    if (value == null || value.isEmpty || int.tryParse(value) == null || int.parse(value) <= 0) {
                      return 'Please enter a valid positive number of days';
                    }
                    return null;
                  },
                ),
              ],
              const SizedBox(height: 30),
              ElevatedButton(
                onPressed: _saveTask,
                style: ElevatedButton.styleFrom(
                  padding: const EdgeInsets.symmetric(vertical: 15),
                ),
                child: const Text('Save Task', style: TextStyle(fontSize: 18)),
              ),
            ],
          ),
        ),
      ),
    );
  }
}

