            // Task Streaks Placeholder (bottom right) - positioned within a Row/Column for better control
            Padding(
              padding: const EdgeInsets.all(8.0),
              child: Align(
                alignment: Alignment.bottomRight,
                child: Container(
                  padding: const EdgeInsets.all(8.0),
                  color: Colors.blueGrey[100],
                  child: const Text('Streaks: Max (0), Current (0)', style: TextStyle(color: Colors.blueGrey)),
                ),
