            flex: 3,
            child: Row(
              children: <Widget>[
                // Daily Tasks Column
                Expanded(
                  flex: 1,
                  child: Container(
                    margin: const EdgeInsets.all(8.0),
                    decoration: BoxDecoration(
                      color: Colors.lightBlue[50], // Sky-blueish background
                      borderRadius: BorderRadius.circular(8.0),
                      border: Border.all(color: Colors.blueAccent),
                    ),
                    child: Padding(
                      padding: const EdgeInsets.all(8.0),
                      child: Column(
                        crossAxisAlignment: CrossAxisAlignment.start,
                        children: [
                          Row(
                            mainAxisAlignment: MainAxisAlignment.spaceBetween,
                            children: [
                              Text('Daily Tasks', style: Theme.of(context).textTheme.titleMedium),
                              IconButton(
                                icon: const Icon(Icons.add),
                                onPressed: () async {
                                  await Navigator.of(context).push(
                                    MaterialPageRoute(builder: (_) => const TaskFormScreen(initialTaskType: TaskType.daily)),
                                  );
                                  _initializeTodaysData();
                                },
                              ),
                            ],
                          ),
                          Expanded( // Allows ListView.builder to take available height
